% dcdphi = fmo*cos(phi)*((351843720888320*Lmo*exp((10*(Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2))/Lmo - 10)*cos(phi)*sin(phi))/(5221823812744079*(Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2)) - (40*Lmo*a*exp(-(20*((Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2)/(Lmo*(lambda*(a/2 - 1) - 1)) + 1)^2)/9)*cos(phi)*sin(phi)*((Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2)/(Lmo*(lambda*(a/2 - 1) - 1)) + 1))/(9*(lambda*(a/2 - 1) - 1)*(Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2))) - fmo*sin(phi)*((35184372088832*exp((10*(Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2))/Lmo - 10))/5221823812744079 + a*exp(-(20*((Lmo^2*sin(phi)^2 + (Lmt - lts)^2)^(1/2)/(Lmo*(lambda*(a/2 - 1) - 1)) + 1)^2)/9));
